mapper.select:
SELECT
SUM(CASE WHEN pq_code LIKE 'PT%' THEN 1 ELSE 0 END) as ptCount,
SUM(CASE WHEN pq_code LIKE 'GX%' THEN 1 ELSE 0 END) as gxCount,
SUM(CASE WHEN pq_code LIKE 'GP%' THEN 1 ELSE 0 END) as gpCount
FROM PASSDB


        AND A.USER_NAME Like CONCAT('%', #{ text }, '%')


    and QUARTER(pass_start) = 1;


    and QUARTER(pass_start) = 2;


    and QUARTER(pass_start) = 3;


    and QUARTER(pass_start) = 4;

SELECT F.user_code, E.USER_CODE as emp_code, F.USER_NAME, F.USER_ID, E.user_pic, B.pq_code, B.pq_name, C.pass_start, C.pass_end, B.pq_price, D.sales_date, D.sales_method, E.user_etc, E.user_name as emp_name FROM validatedb A LEFT JOIN passdb C ON C.pass_code = A.pass_code LEFT JOIN userdb E ON E.user_code = C.user_code LEFT JOIN salesdb D ON D.user_code = A.user_code left join userdb F on F.user_code = D.user_code LEFT JOIN passqualdb B ON C.pq_code = B.pq_code
AND F.USER_NAME Like CONCAT('%', #{ text }, '%')
order by C.pass_start desc , D.sales_date desc;
