mapper.insert:
INSERT INTO PAINDB(
    USER_CODE
    , PAIN_POS
)
VALUES(
    #{ userCode }
    , #{ pos }
)

INSERT INTO PAINDB(
    USER_CODE
    , PAIN_POS
    , PAIN_TYPE
    , PAIN_DUR
    , PAIN_CAUSE
    , PAIN_STR
    , PAIN_ETC
)
SELECT
    userCode
    , pos
    , #{ type }
    , #{ dur }
    , #{ cause }
    , #{ str }
    , #{ etc }
FROM
    PAINDB
WHERE
    userCode = #{ userCode }
    AND pos = #{ pos }

mapper.delete:
DELETE FROM
    PAINDB
WHERE
    USER_CODE = #{ code}
    AND PAIN_POS = #{ pos }
